  `freeze_points` int(10) unsigned DEFAULT '0' COMMENT '冻结积分,支付后,用户确认收货前的中间状态',
  `rank_points` int(10) unsigned DEFAULT '0' COMMENT '等级积分',
  PRIMARY KEY (`id`),
  KEY `user_login_key` (`user_login`),
  KEY `user_nicename` (`user_nicename`)
) ENGINE=MyISAM AUTO_INCREMENT=19 DEFAULT CHARSET=utf8;

