DROP TABLE IF EXISTS `sp_order`;
CREATE TABLE `sp_order` (
  `order_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '订单id',
  `order_sn` varchar(20) NOT NULL DEFAULT '0' COMMENT '订单编号',
  `user_id` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '购买用户的id',
  `order_status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0为未确认,1为已确认,2为取消订单,3为无效订单,4为退货,5为已分单,6为部分分单',
  `shipping_status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0为未发货,1为已发货',
  `pay_status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0为未付款,1为已付款',
  `country` smallint(5) unsigned NOT NULL COMMENT '国家代码',
  `province` smallint(5) unsigned NOT NULL COMMENT '省份代码',
  `city` smallint(5) unsigned NOT NULL COMMENT '城市代码',
  `district` smallint(5) NOT NULL COMMENT '区/县代码',
  `address` varchar(255) NOT NULL COMMENT '完整收获地址',
  `consignee` varchar(50) NOT NULL COMMENT '收货人名字',
  `best_time` varchar(200) DEFAULT '' COMMENT '最佳送货时间',
  `zipcode` varchar(60) DEFAULT '' COMMENT '邮编',
  `tel` varchar(60) DEFAULT '' COMMENT '电话',
  `mobile` varchar(60) DEFAULT '' COMMENT '手机',
  `email` varchar(60) DEFAULT '' COMMENT '邮箱',
  `pay_id` mediumint(8) unsigned NOT NULL COMMENT '付款方式的id',
  `pay_name` varchar(120) NOT NULL COMMENT '付款名称',
  `money_goods` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品总价格',
  `money_total` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '订单总价格(商品总价+各种费用-各种优惠折扣)',
  `paid_money` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '已付款',
  `paid_points` int(10) unsigned DEFAULT '0',
  `paid_coupon` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '已经支付的coupon数额',
  `pack_fee` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '打包费用',
  `pay_fee` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '付款手续费',
  `shipping_fee` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '快递费用',
  `insure_fee` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '保险费用',
  `package_fee` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '包装费用',
  `card_fee` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '贺卡费用',
  `receipt_fee` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '发票税额',
  `discount` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '折扣金额',
  `receipt_type` varchar(20) DEFAULT '' COMMENT '发票类型',
  `receipt_head` varchar(50) DEFAULT '' COMMENT '发票抬头',
  `receipt_content` varchar(255) DEFAULT '' COMMENT '发票内容',
  `package_id` tinyint(1) unsigned DEFAULT '0' COMMENT '包装类型',
  `package_name` varchar(20) DEFAULT '' COMMENT '包装名称',
  `add_time` int(10) unsigned NOT NULL COMMENT '订单创建时间',
  `pay_time` int(10) unsigned NOT NULL COMMENT '付款时间',
  `shipping_id` tinyint(2) unsigned DEFAULT '0' COMMENT '快递id',
  `shipping_name` varchar(100) NOT NULL DEFAULT '' COMMENT '快递名称',
  `shipping_sn` varchar(50) NOT NULL DEFAULT '' COMMENT '运单号',
  `shipping_time` int(10) unsigned NOT NULL COMMENT '发货时间',
  `message` varchar(255) DEFAULT '' COMMENT '买家留言',
  `message_admin` varchar(255) DEFAULT '' COMMENT '管理员给用户留言',
  `receive_time` int(10) unsigned DEFAULT '0' COMMENT '收获时间',
  `how_oos` varchar(50) DEFAULT '等待所有商品备其后再发' COMMENT '缺少商品的发货方式',
  `card_id` tinyint(1) unsigned DEFAULT '0' COMMENT '贺卡类型',
  `card_name` varchar(20) DEFAULT '' COMMENT '卡片名称',
  `card_message` varchar(255) DEFAULT '' COMMENT '贺卡留言内容',
  `is_insure` tinyint(1) unsigned DEFAULT '0' COMMENT '是否保价',
  `weight` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品重量',
  `user_status` tinyint(1) unsigned DEFAULT '0' COMMENT '0为显示,1为不显示',
  `coupon` varchar(255) DEFAULT '',
  `ref` varchar(20) DEFAULT '网站' COMMENT '来源',
  PRIMARY KEY (`order_id`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;

