DROP TABLE IF EXISTS `sp_resume`;
CREATE TABLE `sp_resume` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `uid` int(11) unsigned NOT NULL,
  `title` varchar(60) CHARACTER SET utf8 NOT NULL COMMENT '简历名称',
