  `status` varchar(255) DEFAULT '1' COMMENT '1正常 0关闭 2审核不通过',
  `addtime` varchar(10) CHARACTER SET utf8 NOT NULL COMMENT '创建时间',
  `refreshtime` varchar(10) CHARACTER SET utf8 NOT NULL COMMENT '刷新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=latin1;

